function openTab(e,t){var l,s,o;for(s=document.getElementsByClassName("tabcontent"),l=0;l<s.length;l++)s[l].style.display="none";for(o=document.getElementsByClassName("tablinks"),l=0;l<o.length;l++)o[l].className=o[l].className.replace("active","");document.getElementById(t).style.display="block",e.currentTarget.className+=" active"}function openModal(){document.querySelector(".modal").classList.add("show")}function closeModal(){document.querySelector(".modal").classList.remove("show")}function openFilters(){var e=document.querySelector(".show-filters");"block"===e.style.display?e.style.display="none":e.style.display="block"}